import {
    GET_CARD_TYPES
} from "../actions/options";

const initState = [];

function cardTypeReducer(cardTypes = initState, action){
    const {option, payload} = action;

    switch(option){
        case GET_CARD_TYPES:
            return [...cardTypes, payload];

        default:
            return cardTypes;
    }
}

export default cardTypeReducer;
